import React from 'react';
import { Table, Button } from 'reactstrap';
import variables from './../variables';

function Verification({ students, setStudents }) {

 const handleConfirm = (studentId) => {
    // Send a request to the server to confirm the student
    fetch(variables.API_URL + 'user/' + studentId, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ verified: true })
    })
      .then(response => response.json())
      .then(data => {
        // Show a modal
        window.alert("Verification completed successfully");

        // Remove the confirmed student from the list
        setStudents(students.filter(student => student.id !== studentId));

        // Reload the page
        window.location.reload();
      })
      .catch(error => {
        console.error('Failed to confirm student:', error);
      });
 };

 const handleDelete = (studentId) => {
    // Send a request to the server to delete the student
    fetch(variables.API_URL + 'user/' + studentId, {
      method: 'DELETE'
    })
      .then(response => {
        // Remove the deleted student from the list
        setStudents(students.filter(student => student.id !== studentId));
      })
      .catch(error => {
        console.error('Failed to delete student:', error);
      });
 };

 return (
    <Table striped>
      <thead>
        <tr>
          <th>#</th>
          <th>ID</th>
          <th>Name</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {students.map((student, index) => (
          <tr key={student.id}>
            <td>{index + 1}</td>
            <td>{student.id}</td>
            <td>{student.first_name} {student.last_name}</td>
            <td>
              <Button color="success" onClick={() => handleConfirm(student.id)}>Confirm</Button>
              <Button color="danger" onClick={() => handleDelete(student.id)}>Delete</Button>
            </td>
          </tr>
        ))}
      </tbody>
    </Table>
 );
}

export default Verification;
