import React,{Component} from "react";
import variables from './../variables';
import { Button, Table, Modal, ModalHeader, ModalBody, FormGroup, Label, Input, ModalFooter, Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';


function getMonthName(monthNumber) {
  const months = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];
  return months[monthNumber - 1];
}

export class Roster extends Component {
    constructor(props) {
        super(props);
        this.state = {
            roster_id: 0,
            month: "",
            year:"",
            modalTitle:"",
            doctor: {
                doctor_id:"",
                doctor_name:"",
            },
            rosters:[],
            groupedByDay: {},
            dayslot:{
                day:"",
                slot:"",
            },
            doctors:[],
            dayslots:[]
        }
    }
    refreshList() {
        fetch(variables.API_URL + 'roster/') 
          .then(response => response.json())
          .then(data => {
            if ('data' in data && Array.isArray(data.data)) {
              // Group the rosters by day
              const groupedByDay = data.data.reduce((acc, roster) => {
                const day = roster.dayslot.day;
                if (!acc[day]) {
                  acc[day] = [];
                }
                acc[day].push(roster);
                return acc;
              }, {});
    
              this.setState({
                rosters: data.data,
                groupedByDay: groupedByDay // Store the grouped rosters in the state
              });
            } else {
              console.error('Expected an object with a data property containing an array, but got ', data);
            }
          });


          fetch(variables.API_URL + 'dayslot/') 
            .then(response => response.json())
            .then(data => {
                if ('data' in data && Array.isArray(data.data)) {
                const dayslots = data.data.map(dayslot => {
                    return {
                    id: dayslot.id,
                    day: dayslot.day,
                    slot: dayslot.slot
                    };
                });
                console.log(dayslots); // Log the mapped dayslots array
                this.setState({
                    dayslots: dayslots
                });
                } else {
                console.error('Expected an object with a data property containing an array of dayslot objects, but got ', data);
                }
            });



            fetch(variables.API_URL + 'doctor/') 
            .then(response => response.json())
            .then(data => {
               if ('data' in data && Array.isArray(data.data)) {
                 const doctors = data.data.map(doctor => {
                   return {
                     doctor_id: doctor.doctor_id,
                     doctor_name: doctor.doctor_name,
                     specialization: doctor.specialization
                   };
                 });
                 console.log(doctors); // Log the mapped doctors array
                 this.setState({
                   doctors: doctors
                 });
               } else {
                 console.error('Expected an object with a data property containing an array of doctor objects, but got ', data);
               }
            });
    }
    
    

      componentDidMount() {
        this.refreshList();
        }
      changeRosterId = (e) => {
            this.setState({ roster_id: e.target.value })
        }
        changeMonth = (e) => {
            this.setState({ month: e.target.value })
        }
        changeYear = (e) => {
            this.setState({ year: e.target.value })
        }
        
        addClick() {
            this.setState({
                modalTitle: "Register Roster",
                Roster_id : 0,
            });
        }

        handleDoctorSelection(selectedDoctorId) {
            this.setState({
                selectedDoctorId: selectedDoctorId
            });
        }
        
        handleDayslotSelection(selectedDayslotId) {
            // Update the state
            this.setState({
                selectedDayslotId: selectedDayslotId
            });
        }
        
           
           
        createClick = () => {
            // Construct the URL with the month, year, doctor, and dayslot as parameters
            const url = `${variables.API_URL}roster/?month=${this.state.month}&year=${this.state.year}&doctor=${this.state.selectedDoctorId}&dayslot=${this.state.selectedDayslotId}`;
        
            // Make a GET request to the roster API
            fetch(url)
                .then(res => res.json())
                .then(result => {
                    // If a roster with the same month, year, doctor, and dayslot already exists, show an alert and return
                    if (result.length > 0) {
                        alert('Roster already exists.');
                        return;
                    }
        
                    // Otherwise, make a POST request to create a new roster
                    fetch(variables.API_URL + 'roster/', {
                        method: 'POST',
                        headers: {
                            'Accept': 'application/json',
                            'Content-Type': 'application/json'
                        },
                        body: JSON.stringify({
                            roster_id: this.state.roster_id,
                            month: this.state.month,
                            year: this.state.year,
                            doctor: this.state.selectedDoctorId,
                            dayslot: this.state.selectedDayslotId
                        })
                    })
                    .then(res => res.json())
                    .then(result => {
                        alert(JSON.stringify(result));
                        this.refreshList();
                    }, (error) => {
                        alert('Failed');
                    })
                })
        }
        
        
        
           
        
        
        

        deleteClick(id) {
            if (window.confirm('Are you sure?')) {
                fetch(variables.API_URL + 'roster/' + id, {
                    method: 'DELETE',
                    headers: {
                        'Accept': 'application/json',
                        'Content-Type': 'application/json'
                    }
                })
                    .then(res => res.json())
                    .then(result => {
                        alert(result);
                        this.refreshList();
    
                    }, (error) => {
                        alert('Failed');
                    })
            }
        }
        render() {
            const {
                rosters,
                modalTitle,
                groupedByDay, 
                roster_id,
                month,
                year,
                doctors,
                dayslots,
                doctor: {
                    doctor_id,
                    doctor_name,
                },
                dayslot:{
                    day,
                    slot,
                }
            } = this.state;
            
           
            return (
                <div>
      
                     <button type="button"
                         className='btn btn-primary m-2 float-start'
                         >
                            Month : {getMonthName(rosters[0]?.month)}
                    </button>
                    <button type="button"
                         className='btn btn-primary m-2 float-end'
                         data-bs-toggle='modal'
                         data-bs-target='#exampleModal'
                         onClick={() => this.addClick()}>
                            Register Roster
                    </button>
                    <div className="mt-3 mx-auto" style={{maxWidth: '90%'}}>
                    <Table striped style={{ marginTop: '20px' }}>
                        <thead>
                            <tr>
                                <th>
                                    Roster Id
                                </th>
                                <th>
                                    Day
                                </th>
                                <th>
                                    Slot
                                </th>
                                
                                <th>
                                    Doctor Name
                                </th>
                                <th>
                                    Options
                                </th>
                            </tr>
                        </thead>
                        <tbody>
                        {Object.keys(groupedByDay).map((day, dayIndex) => (
                        groupedByDay[day].map((roster, rosterIndex) => (
                            <tr key={roster.roster_id}>
                                     <td>{rosterIndex === 0 ? dayIndex + 1 : ''}</td>
                                <td>{rosterIndex === 0 ? day : ''}</td>
                                <td>{roster.dayslot.slot}</td>
                                
                                <td>{roster.doctor.doctor_name}</td>
                                    
                                    <td>
                                        {/* <button type='button'
                                        className='btn btn-light mr-1'
                                        data-bs-toggle='modal'
                                        data-bs-target='#exampleModal'
                                        onClick={() => this.editClick(doc)}>
                                        <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-pencil-square" viewBox="0 0 16 16">
                                            <path d="M15.502 1.94a.5.5 0 0 1 0 .706L14.459 3.69l-2-2L13.502.646a.5.5 0 0 1 .707 0l1.293 1.293zm-1.75 2.456-2-2L4.939 9.21a.5.5 0 0 0-.121.196l-.805 2.414a.25.25 0 0 0 .316.316l2.414-.805a.5.5 0 0 0 .196-.12l6.813-6.814z" />
                                            <path fillRule="evenodd" d="M1 13.5A1.5 1.5 0 0 0 2.5 15h11a1.5 1.5 0 0 0 1.5-1.5v-6a.5.5 0 0 0-1 0v6a.5.5 0 0 1-.5.5h-11a.5.5 0 0 1-.5-.5v-11a.5.5 0 0 1 .5-.5H9a.5.5 0 0 0 0-1H2.5A1.5 1.5 0 0 0 1 2.5v11z" />
                                        </svg>
                                        </button>*/}
                                        <button type='button'
                                                className='btn btn-light mr-1'
                                                onClick={() => this.deleteClick(roster.roster_id)}>
                                                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-trash-fill" viewBox="0 0 16 16">
                                                    <path d="M2.5 1a1 1 0 0 0-1 1v1a1 1 0 0 0 1 1H3v9a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V4h.5a1 1 0 0 0 1-1V2a1 1 0 0 0-1-1H10a1 1 0 0 0-1-1H7a1 1 0 0 0-1 1H2.5zm3 4a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 .5-.5zM8 5a.5.5 0 0 1 .5.5v7a.5.5 0 0 1-1 0v-7A.5.5 0 0 1 8 5zm3 .5v7a.5.5 0 0 1-1 0v-7a.5.5 0 0 1 1 0z" />
                                                </svg>
                                        </button>
                                    </td>  
                                </tr>
                        ))
                            ))} 
                        </tbody>
                    </Table>
                    </div>
                
                     <div className='modal fade' id="exampleModal" tabIndex="-1" aria-hidden="true">
                        <div className='modal-dialog modal-lg modal-dialog-centered'>
                            <div className='modal-content'>
                                <div className='modal-header'>
                                    <h5 className='modal-title'>{modalTitle}</h5>
                                    <button type='button' className='btn-close' data-bs-dismiss='modal' aria-label='Close'></button>
                                </div>

                                <div className='modal-body'>
                                    <div className='d-flex flex-row bd-highlight mb-3'>
                                        <div className='p-2 w-50 bd-highlight'>
                                        <div className='input-group mb-3'>
                                            <span className='input-group-text'>Month</span>
                                            <select className='form-control' value={this.state.month} onChange={this.changeMonth}>
                                                <option value="">Select Month</option>
                                                <option value="1">January</option>
                                                <option value="2">February</option>
                                                <option value="3">March</option>
                                                <option value="4">April</option>
                                                <option value="5">May</option>
                                                <option value="6">June</option>
                                                <option value="7">July</option>
                                                <option value="8">August</option>
                                                <option value="9">September</option>
                                                <option value="10">October</option>
                                                <option value="11">November</option>
                                                <option value="12">December</option>
                                            </select>
                                        </div>
                                            <div className='input-group mb-3'>
                                                <span className='input-group-text'>Year</span>
                                                <input type='text'
                                                    className='form-control'
                                                    value={year}
                                                    onChange={this.changeYear} />
                                            </div>
                                            <div className='input-group mb-3'>
                                                <span className='input-group-text'>Doctor</span>
                                                <div className="dropdown">
                                                <button className="btn btn-light dropdown-toggle" type="button" id="dropdownMenuButton" data-bs-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                                    {this.state.selectedDoctorId ? this.state.selectedDoctorId : "Select Doctor"}
                                                </button>
                                                <ul className="dropdown-menu" aria-labelledby="dropdownMenuButton" style={{maxHeight: '200px', overflowY: 'auto'}}>
                                                    {this.state.doctors.map((doctor) => (
                                                    <li key={doctor.doctor_id}>
                                                        <a className="dropdown-item" href="#" onClick={() => this.handleDoctorSelection(doctor.doctor_id)}>{doctor.doctor_name} - {doctor.specialization}</a>
                                                    </li>
                                                    ))}
                                                </ul>
                                                </div>
                                                </div>
                                                <div className='input-group mb-3'>
                                                    <span className='input-group-text'>DaySlot</span>
                                                    <div className="dropdown">
                                                    <button className="btn btn-light dropdown-toggle" type="button" id="dropdownMenuButton" data-bs-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                                                        {this.state.selectedDayslotId ? this.state.selectedDayslotId : "Select Dayslot"}
                                                    </button>
                                                    <ul className="dropdown-menu" aria-labelledby="dropdownMenuButton" style={{maxHeight: '200px', overflowY: 'auto'}}>
                                                        {this.state.dayslots.map((dayslot) => (
                                                        <li key={dayslot.id}>
                                                            <a className="dropdown-item" href="#" onClick={() => this.handleDayslotSelection(dayslot.id)}>{dayslot.day}-{dayslot.slot}</a>
                                                        </li>
                                                        ))}
                                                    </ul>
                                                    </div>
                                                    </div>
                                            </div>
                                        </div>
                                    {
                                        <button type='button'
                                            className='btn btn-primary float-start'
                                            onClick={() => this.createClick()}>
                                            Register
                                        </button>
                                    }
                            </div>
                        </div>
                    </div>
                </div> 
                                   
            </div>
        )

    }   
}
export default Roster;
