import React, { useState } from 'react';
import { Table, Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import variables from './../variables';

function Verification({ students, setStudents }) {
 const [selectedStudent, setSelectedStudent] = useState(null);
 const [modal, setModal] = useState(false);

 const toggle = () => setModal(!modal);

 const handleConfirm = (studentId) => {
    // Send a request to the server to confirm the student
    fetch(variables.API_URL + 'user/' + studentId, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ verified: true })
    })
      .then(response => response.json())
      .then(data => {
        // Show a modal
        window.alert("Verification completed successfully");

        // Remove the confirmed student from the list
        setStudents(students.filter(student => student.id !== studentId));

        // Close the modal
        toggle();

        // Reload the page
        window.location.reload();
      })
      .catch(error => {
        console.error('Failed to confirm student:', error);
      });
 };

 const handleDelete = (studentId) => {
    // Send a request to the server to delete the student
    fetch(variables.API_URL + 'user/' + studentId, {
      method: 'DELETE'
    })
      .then(response => {
        // Remove the deleted student from the list
        setStudents(students.filter(student => student.id !== studentId));

        // Close the modal
        toggle();
      })
      .catch(error => {
        console.error('Failed to delete student:', error);
      });
 };

 const handleSeeDetails = (student) => {
    setSelectedStudent(student);
    toggle();
 };

 return (
    <div style={{ margin: '20px', padding: '20px' }}>
      <Table striped style={{ marginTop: '20px' }}>
        <thead>
          <tr>
            <th>#</th>
            <th>ID</th>
            <th>Name</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student, index) => (
            <tr key={student.id}>
              <td>{index + 1}</td>
              <td>{student.id}</td>
              <td>{student.first_name} {student.last_name}</td>
              <td>
                <Button color="primary" onClick={() => handleSeeDetails(student)}>See Details</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      <Modal isOpen={modal} toggle={toggle}>
        <ModalHeader toggle={toggle}>Student Details</ModalHeader>
        <ModalBody>
          {selectedStudent && (
            <div>
              <p>ID: {selectedStudent.id}</p>
              <p>First Name: {selectedStudent.first_name}</p>
              <p>Last Name: {selectedStudent.last_name}</p>
              <p>Email: {selectedStudent.email}</p>
              <p>Gender: {selectedStudent.gender}</p>
              <p>Department: {selectedStudent.department}</p>
              <p>Phone: {selectedStudent.phone}</p>
              <p>Permanent Address: {selectedStudent.permanent_address}</p>
              <p>Hall Name: {selectedStudent.hall_name}</p>
              <p>Room No: {selectedStudent.room_no}</p>
              <p>Image: {selectedStudent.image}</p>
              <p>Blood Group: {selectedStudent.blood_group}</p>
              <p>Transaction ID: {selectedStudent.transaction_id}</p>
              <p>Created At: {selectedStudent.created_at}</p>
              <p>Updated At: {selectedStudent.updated_at}</p>
              <p>Is Staff: {selectedStudent.is_staff ? 'Yes' : 'No'}</p>
              <p>Is Superuser: {selectedStudent.is_superuser ? 'Yes' : 'No'}</p>
              <p>Is Active: {selectedStudent.is_active ? 'Yes' : 'No'}</p>
            </div>
          )}
        </ModalBody>
        <ModalFooter>
          <Button color="success" onClick={() => handleConfirm(selectedStudent.id)}>Confirm</Button>{' '}
          <Button color="danger" onClick={() => handleDelete(selectedStudent.id)}>Delete</Button>{' '}
          <Button color="secondary" onClick={toggle}>Cancel</Button>
        </ModalFooter>
      </Modal>
    </div>
 );
}

export default Verification;
