import { Navigate, Outlet } from 'react-router-dom';

function PrivateRoute({ children }) {
 const token = localStorage.getItem('token');
 const user = JSON.parse(localStorage.getItem('user'));

 if (!token || !user) {
    return <Navigate to="/" />;
 }

 const { is_staff, is_superuser, is_active, verified } = user;

 if (!is_active || !verified) {
    return <Navigate to="/" />;
 }

 // Check the user's role
 if ((is_staff || is_superuser) && (window.location.pathname.startsWith("/admin"))) {
    return children;
 } else if (!is_staff && !is_superuser && (window.location.pathname.startsWith("/student"))) {
    return children;
 } else {
    return <Navigate to="/" />;
 }
}

export default PrivateRoute;
