import React, { Component } from "react";
import variables from './../variables';
import { Button, Table, Modal, ModalHeader, ModalBody, FormGroup, Label, Input, ModalFooter, Dropdown, DropdownToggle, DropdownMenu, DropdownItem, Form, Row, Col } from 'reactstrap';

export class PrescriptionForm extends Component {
    constructor(props) {
        super(props);
        this.state = {
            prescription_id: 0,
            complaints: "",
            diagnosis: "",
            rx: "",
            investigation: "",
            lifestyle: "",
            last_checkup_id: "",
            pulse_rate: "",
            bp: "",
            temp: "",
            weight: "",
            age: "",
            confirmation: "",
            doctor: {
                doctor_id: "",
                doctor_name: "",
            },
            booklet: {
                booklet_id: "",
            },
            booklets: [],
            doctors: [],
            prescriptions: []

        }
    }


    refreshList() {
        fetch(variables.API_URL + 'prescription/')
            .then(response => response.json())
            .then(data => {
                if ('data' in data && Array.isArray(data.data)) {
                    console.log(data)
                    this.setState({
                        prescriptions: data.data

                    });
                } else {
                    console.error('Expected an object with a data property containing a single booklet object, but got ', data);
                }
            });

        fetch(variables.API_URL + 'ebooklet/')
            .then(response => response.json())
            .then(data => {
                if ('data' in data && Array.isArray(data.data)) {
                    this.setState({
                        booklets: data.data
                    });
                } else {
                    console.error('Expected an object with a data property containing a single booklet object, but got ', data);
                }
            });

        fetch(variables.API_URL + 'doctor/')
            .then(response => response.json())
            .then(data => {
                if ('data' in data && Array.isArray(data.data)) {
                    const doctors = data.data.map(doctor => {
                        return {
                            doctor_id: doctor.doctor_id,
                            doctor_name: doctor.doctor_name,
                            specialization: doctor.specialization
                        };
                    });
                    console.log(doctors); // Log the mapped doctors array
                    this.setState({
                        doctors: doctors
                    });
                } else {
                    console.error('Expected an object with a data property containing an array of doctor objects, but got ', data);
                }
            });
    }
    componentDidMount() {
        this.refreshList();
    }
    toggleDropdownDoctor = () => {
        this.setState(prevState => ({
            dropdownOpenDoctor: !prevState.dropdownOpenDoctor
        }));
    };
    handleDoctorSelection(selectedId) {
        this.setState({
            selectedDoctorId: selectedId
        });
    }
    changeLast_checkup_id = (e) => {
        this.setState({ last_checkup_id: e.target.value })
    }
    changePulse_rate = (e) => {
        this.setState({ pulse_rate: e.target.value })
    }


    render() {
        const {

            prescription_id,
            complaints,
            diagnosis,
            rx,
            investigation,
            lifestyle,
            last_checkup_id,
            pulse_rate,
            bp,
            temp,
            weight,
            age,
            confirmation,
            doctors,
            showModal,
            doctor: {
                doctor_id,
                doctor_name,
                specialization,
            },

            selectedDoctorId,

        } = this.state;
        return (
            <div style={{
                display: 'flex',
                justifyContent: 'center',
                alignItems: 'center',
                height: '100vh',
                marginTop: '50px'

            }}>
                <Form>
                    <Row>
                        <Col md="4">
                            <FormGroup>
                                <Label for="doctor">Doctor</Label>
                                <Dropdown isOpen={this.state.dropdownOpenDoctor} toggle={this.toggleDropdownDoctor}>
                                    <DropdownToggle caret>
                                        {this.state.selectedDoctorId ? this.state.selectedDoctorId : "Select Doctor"}
                                    </DropdownToggle>
                                    <DropdownMenu>
                                        {this.state.doctors.map((doctor) => (
                                            <DropdownItem key={doctor.doctor_id} onClick={() => this.handleDoctorSelection(doctor.doctor_id)}>
                                                {doctor.doctor_name} - {doctor.specialization}
                                            </DropdownItem>
                                        ))}
                                    </DropdownMenu>
                                </Dropdown>
                            </FormGroup></Col>

                    </Row>

                    <Row>
                        <FormGroup>
                            <Label for="complaints">Complaints</Label>
                            <Input type="textarea" name="complaints" id="complaints" value={this.state.complaints} onChange={this.changeComplaints} />
                        </FormGroup>
                    </Row>




                    <Row>
                        <Col md={4}>
                            <FormGroup>
                                <Label for="last_checkup_id">Last Checkup Id</Label>
                                <Input type="number" name="last_checkup_id" id="last_checkup_id" value={this.state.last_checkup_id} onChange={this.changeLast_checkup_id} />
                            </FormGroup>

                        </Col>
                        <Col md={4}>
                            <FormGroup>
                                <Label for="pulse_rate">Last Checkup Id</Label>
                                <Input type="number" name="pulse_rate" id="pulse_rate" value={this.state.pulse_rate} onChange={this.changePulse_rate} />
                            </FormGroup>
                        </Col>
                        <Col md={4}>
                            <FormGroup>
                                <Label for="bp">Blood Pressure</Label>
                                <Input type="number" name="bp" id="bp" value={this.state.bp} onChange={this.changeBp} />
                            </FormGroup>
                        </Col>
                    </Row>
                    <Row>
                        <Col md={4}>
                            <FormGroup>
                                <Label for="temp">Temperature</Label>
                                <Input type="number" name="temp" id="temp" value={this.state.temp} onChange={this.changeTemp} />
                            </FormGroup>
                        </Col>
                        <Col md={4}>
                            <FormGroup>
                                <Label for="weight">Weight</Label>
                                <Input type="number" name="weight" id="weight" value={this.state.weight} onChange={this.changeWeight} />
                            </FormGroup>
                        </Col>
                        <Col md={4}>
                            <FormGroup>
                                <Label for="age">Age</Label>
                                <Input type="number" name="age" id="age" value={this.state.age} onChange={this.changeAge} />
                            </FormGroup>
                        </Col>
                    </Row>
                    <Row>
                        <FormGroup>
                            <Label for="diagnosis">Diagnosis</Label>
                            <Input type="textarea" name="diagnosis" id="diagnosis" value={this.state.diagnosis} onChange={this.changeDiagnosis} />
                        </FormGroup>
                    </Row>
                    <Row>
                        <FormGroup row>
                            <Label
                                for="exampleText"
                            >
                                <h4>R<sub>x</sub></h4>
                            </Label>
                            <Input type="textarea" name="rx" id="rx" value={this.state.rx} onChange={this.changeRx} />
                        </FormGroup>
                    </Row>
                    <Row>
                        <FormGroup>
                            <Label for="investigation">Investigation</Label>
                            <Input type="textarea" name="investigation" id="investigation" value={this.state.investigation} onChange={this.changeInvestigation} />
                        </FormGroup>
                    </Row>
                    <Row>
                        <FormGroup>
                            <Label for="lifestyle">Lifestyle</Label>
                            <Input type="textarea" name="lifestyle" id="lifestyle" value={this.state.lifestyle} onChange={this.changeLifestyle} />
                        </FormGroup>
                    </Row>
                    <Row>

                    </Row>
                    <Button>
                        Submit
                    </Button>
                </Form>
            </div>
        );
    }
}

export default PrescriptionForm;
