import React, { useState } from 'react';
import {
 Button,
 Form,
 FormGroup,
 Input,
 Label,
 Modal,
 ModalHeader,
 ModalBody
} from 'reactstrap';
import { useNavigate } from 'react-router-dom';
import variables from './variables'; 

function Login() {
 const navigate = useNavigate();
 const [id, setId] = useState('');
 const [password, setPassword] = useState('');
 const [modal, setModal] = useState(false);

 const toggleModal = () => setModal(!modal);

 const handleSubmit = async (event) => {
    event.preventDefault();
    try {
       const response = await fetch(`${variables.API_URL}login/`, {
         method: 'POST',
         headers: {
           'Content-Type': 'application/json'
         },
         body: JSON.stringify({ id, password })
       });
   
       if (!response.ok) {
         throw new Error(`HTTP error! status: ${response.status}`);
       }
   
       const user = await response.json();
       if (user.is_staff && user.is_superuser && user.is_active && user.verified) {
         navigate('/admin/home');
       } else if (!user.is_staff && !user.is_superuser && user.is_active && user.verified) {
         navigate('/student/home');
       } else {
         toggleModal(); // Show the modal
       }
    } catch (error) {
       console.error("Failed to login: ", error);
    }
   };

 return (
    <Form onSubmit={handleSubmit}>
      <FormGroup>
        <Label for="id">ID</Label>
        <Input type="text" name="id" id="id" placeholder="Enter ID" value={id} onChange={e => setId(e.target.value)} />
      </FormGroup>
      <FormGroup>
        <Label for="password">Password</Label>
        <Input type="password" name="password" id="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
      </FormGroup>
      <Button type="submit">Login</Button>
      <Modal isOpen={modal} toggle={toggleModal}>
       <ModalHeader toggle={toggleModal}>Not Verified</ModalHeader>
       <ModalBody>
          You are not verified. Please verify your account to continue.
       </ModalBody>
      </Modal>
    </Form>
 );
}

export default Login;
