import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Card, CardBody, CardTitle, CardText, Button, Modal, ModalHeader, ModalBody, ModalFooter, Form, FormGroup, Label, Input } from 'reactstrap';
import variables from './../variables';
import PropTypes from 'prop-types';

function StudentHome() {
 const navigate = useNavigate();
 const user = JSON.parse(localStorage.getItem('user'));
 const [modal, setModal] = useState(false);
 const [formData, setFormData] = useState(user);

 const toggle = () => setModal(!modal);

 const handleInputChange = (event) => {
    setFormData({ ...formData, [event.target.name]: event.target.value });
 };

 const handleUpdate = (event) => {
    event.preventDefault();

    fetch(variables.API_URL + 'user/' + user.id , {
        method: 'PATCH',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
    })
    .then(res => res.json())
    .then(result => {
        const userData = result.data;
        localStorage.setItem('user', JSON.stringify(userData));
        alert('Profile updated successfully!');
        toggle();
    }, (error) => {
        alert('Failed to update profile');
    })
};

 return (
<div>
      <Card className="mb-3">
        <Row noGutters>
          <Col md="4">
            <img src={variables.CLOUDINARY_BASE_URL + user.image} alt="Student" style={{ width: '100%', height: 'auto' }} />
          </Col>
          <Col md="8">
            <CardBody>
              <CardTitle tag="h2">Welcome, {user.first_name} {user.last_name}</CardTitle>
              <CardText>ID: {user.id}</CardText>
              <CardText>Email: {user.email}</CardText>
              <CardText>Gender: {user.gender}</CardText>
              <CardText>Department: {user.department}</CardText>
              <CardText>Phone: {user.phone}</CardText>
              <CardText>Permanent Address: {user.permanent_address}</CardText>
              <CardText>Hall Name: {user.hall_name}</CardText>
              <CardText>Room No: {user.room_no}</CardText>
              <CardText>Blood Group: {user.blood_group}</CardText>
              <Button onClick={toggle}>Update Profile</Button>
            </CardBody>
          </Col>
        </Row>
      </Card>

      <Modal isOpen={modal} toggle={toggle}>
        <ModalHeader toggle={toggle}>Update Profile</ModalHeader>
        <ModalBody>
        <Form onSubmit={handleUpdate}>
            <FormGroup>
                <Label for="phone">Phone No.</Label>
                <Input type="text" name="phone" id="phone" placeholder="Enter Phone No." value={formData.phone} onChange={handleInputChange} />
            </FormGroup>
            <FormGroup>
                <Label for="hall_name">Hall Name</Label>
                <Input type="text" name="hall_name" id="hall_name" placeholder="Enter Hall Name" value={formData.hall_name} onChange={handleInputChange} />
            </FormGroup>
            <FormGroup>
                <Label for="room_no">Room No.</Label>
                <Input type="text" name="room_no" id="room_no" placeholder="Enter Room No." value={formData.room_no} onChange={handleInputChange} />
            </FormGroup>
            <Button color="primary" type="submit">Update</Button>
            </Form>
        </ModalBody>
        <ModalFooter>
          <Button color="secondary" onClick={toggle}>Cancel</Button>
        </ModalFooter>
      </Modal>
    </div>
 );
}

StudentHome.propTypes = {
 user: PropTypes.object.isRequired,
 toggle: PropTypes.func.isRequired,
 formData: PropTypes.object.isRequired,
 handleInputChange: PropTypes.func.isRequired,
 handleUpdate: PropTypes.func.isRequired,
};

export default StudentHome;
