import React from 'react';
import { Card, CardBody, CardText } from 'reactstrap';
import { getCurrentDayOfWeek, getCurrentTimeSlot } from './Roster';
import variables from '../variables';

// Function to fetch unverified students count
const fetchUnverifiedCount = () => {
 return fetch(variables.API_URL + 'unverified/')
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(data => data.count) // assuming the response has a 'count' field
    .catch(error => {
      console.error('Error:', error);
    });
};

// Function to fetch the currently available doctor
const fetchAvailableDoctor = () => {
    const currentDayOfWeek = getCurrentDayOfWeek();
    const currentTimeSlot = getCurrentTimeSlot();
   
    return fetch(variables.API_URL + 'roster/')
       .then(response => {
         if (!response.ok) {
           throw new Error('Network response was not ok');
         }
         return response.json();
       })
       .then(roster => {
         if (roster.dayslot && roster.dayslot.day === currentDayOfWeek && roster.dayslot.slot === currentTimeSlot) {
           return roster.doctor;
         } else {
           return null;
         }
       })
       .catch(error => {
         console.error('Error:', error);
       });
};


class Home extends React.Component {
 constructor(props) {
    super(props);
    this.state = {
      unverifiedCount: 0,
      availableDoctor: null,
    };
 }

 componentDidMount() {
    fetchUnverifiedCount().then(count => this.setState({ unverifiedCount: count }));
    fetchAvailableDoctor().then(doctor => this.setState({ availableDoctor: doctor }));
 }

 render() {
    const { unverifiedCount, availableDoctor } = this.state;

    return (
       <div>
         <Card>
           <CardBody>
             <CardText>
               Unverified Students: {unverifiedCount}
             </CardText>
           </CardBody>
         </Card>

         <Card>
           <CardBody>
             <CardText>
               Doctor On Duty: {availableDoctor ? availableDoctor.doctor_name : 'N/A'}
               console.log(availableDoctor):
             </CardText>
           </CardBody>
         </Card>
       </div>
    );
 }
}

export default Home;
